mapper.select:
SELECT * FROM (SELECT NUM, title, pdImg, content, writer, readCnt, regDate, ROWNUM AS RNUM FROM mvc_board_tbl WHERE 1 = 1
AND writer LIKE '%'||#{keyword}||'%' AND TITLE LIKE '%'||#{keyword}||'%' AND CONTENT LIKE '%'||#{keyword}||'%'
ORDER BY NUM) WHERE RNUM >= #{start} and RNUM <= #{end}

SELECT * FROM (SELECT id, password, name, birthday, address, hp, email, regDate, ROWNUM AS RNUM FROM mvc_customer_tbl WHERE authority = 'ROLE_USER'
AND ID LIKE '%'||#{keyword}||'%' AND NAME LIKE '%'||#{keyword}||'%' AND ADDRESS LIKE '%'||#{keyword}||'%'
ORDER BY ID) WHERE RNUM >= #{start} and RNUM <= #{end}

SELECT * FROM (SELECT orderNo, pdName, brand, pdsize, price, quantity, orderStatus, cusName, orderdate, ROWNUM AS RNUM FROM mvc_order_tbl WHERE 1 = 1
AND PDNAME LIKE '%'||#{keyword}||'%' AND BRAND LIKE '%'||#{keyword}||'%' AND CUSNAME LIKE '%'||#{keyword}||'%'
ORDER BY orderNo) WHERE RNUM >= #{start} and RNUM <= #{end}

SELECT * FROM (SELECT orderNo, pdName, brand, pdsize, price, quantity, orderStatus, cusName, orderdate, ROWNUM AS RNUM FROM mvc_order_tbl WHERE orderStatus = '환불요청'
AND PDNAME LIKE '%'||#{keyword}||'%' AND BRAND LIKE '%'||#{keyword}||'%' AND CUSNAME LIKE '%'||#{keyword}||'%'
ORDER BY orderNo) WHERE RNUM >= #{start} and RNUM <= #{end}

SELECT * FROM (SELECT pdNo, pdName, pdImg, brand, pdsize, price, quantity, status, indate, ROWNUM AS RNUM FROM mvc_product_tbl WHERE 1 = 1
AND PDNAME LIKE '%'||#{keyword}||'%' AND BRAND LIKE '%'||#{keyword}||'%' AND STATUS LIKE '%'||#{keyword}||'%'
ORDER BY pdNo) WHERE RNUM >= #{start} and RNUM <= #{end}
